<font BOLD="true" NAME="Verdana" SIZE="16"/>
<node CREATED="1288703930656" FOLDED="true" ID="_" MODIFIED="1404654941170" POSITION="right" TEXT="Keywords">
<arrowlink DESTINATION="_" ENDARROW="Default" ENDINCLINATION="0;0;" ID="Arrow_ID_1588333490" STARTARROW="None" STARTINCLINATION="0;0;"/>
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<icon BUILTIN="full-1"/>
<node CREATED="1288703948171" ID="Freemind_Link_207276196" MODIFIED="1288704153171" TEXT="describes a subject that u enter in a search box."/>
<node CREATED="1288704261031" FOLDED="true" ID="Freemind_Link_827230339" MODIFIED="1341652422798" TEXT="e.g.">
<node CREATED="1288704265437" ID="Freemind_Link_976799503" MODIFIED="1288704295703" TEXT="mentioned several times in the home page."/>
<node CREATED="1288704299015" ID="Freemind_Link_1023125063" MODIFIED="1288704329468" TEXT="mentioned in title and headlines"/>
</node>
<node CREATED="1288704525937" ID="Freemind_Link_947263090" MODIFIED="1288704527843" TEXT="Adding your site&#x2019;s most important keyword to the URL is one solution"/>
<node CREATED="1288704941328" ID="Freemind_Link_1445814845" MODIFIED="1288704948234" TEXT="Note: keyword doesn&#x2019;t have to be a single word.">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
</node>
<node CREATED="1288704954406" ID="Freemind_Link_1476474546" MODIFIED="1288704964468" TEXT="It can be a phrase."/>
<node CREATED="1301824001020" ID="Freemind_Link_934842059" MODIFIED="1321085689411" TEXT="or Keyword Effectiveness Index ">
<node CREATED="1301824023980" ID="Freemind_Link_1810340402" MODIFIED="1301824034877" TEXT="determine whether it&#x2019;s worth targeting in your website"/>
</node>
<node CREATED="1305958583120" ID="ID_1669502560" MODIFIED="1305958617681" TEXT="use most relevant keywoords but with less competition."/>
<node COLOR="#990000" CREATED="1306348482031" FOLDED="true" ID="ID_798034856" MODIFIED="1341650305835" TEXT="weightage">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1306348494417" ID="ID_748265874" MODIFIED="1320776350704" TEXT="9. Keyword in your domain name (7%)"/>
<node CREATED="1306348494405" ID="ID_1980065175" MODIFIED="1306348503758" TEXT="1. Keyword in the Title Tag 6%"/>
<node CREATED="1306348494410" ID="ID_1164670215" MODIFIED="1306348544003" TEXT="2. Keyword as the first word(s) of the Title Tag (6%) "/>
<node CREATED="1306348494411" ID="ID_1491671154" MODIFIED="1306348557377" TEXT="3. Keyword in the Root DomainName (e.g. keyword.com) (6%)"/>
<node CREATED="1306348494412" ID="ID_1477403805" MODIFIED="1306348494412" TEXT="4. Keyword in the H1 Headline Tag (5%)"/>
<node CREATED="1306348494424" ID="ID_1096381367" MODIFIED="1320776378772" TEXT="21. Keywords in Meta tagging (5%)"/>
<node CREATED="1306348494413" ID="ID_895953856" MODIFIED="1306348494413" TEXT="5. Keyword in Internal Link Anchor Text on the Page (4%)"/>
<node CREATED="1306348494414" ID="ID_1254358057" MODIFIED="1306348494414" TEXT="6. Keyword in External Link Anchor Text on the Page (4%)"/>
<node CREATED="1306348494415" ID="ID_1474967750" MODIFIED="1306348494415" TEXT="7. Keyword in the H1 Tag (4%)"/>
<node CREATED="1306348494416" ID="ID_687176423" MODIFIED="1306348494416" TEXT="8. Keyword in the First 100 Words in HTML on the Page (4%)"/>
<node CREATED="1306348494417" ID="ID_1314566166" MODIFIED="1306348494417" TEXT="10. Keyword in a subdomain (excavating.abobcat.com) (4%)"/>
<node CREATED="1306348494418" ID="ID_1908835046" MODIFIED="1306348576012" TEXT="11. Keyword in the page name URL (ww.abobcat.com/excavating.html) (3%)"/>
<node CREATED="1306348494419" ID="ID_1751205465" MODIFIED="1306348494419" TEXT="12. Keyword in other H Tags (3%)"/>
<node CREATED="1306348494420" ID="ID_691910117" MODIFIED="1306348494420" TEXT="13. Keyword in image ALT text (3%)"/>
<node CREATED="1306348494421" ID="ID_1066073638" MODIFIED="1306348494421" TEXT="14. Keyword repeating in the HTML Text on the Page (3%)"/>
<node CREATED="1306348494421" ID="ID_364719204" MODIFIED="1306348494421" TEXT="15. Keyword in image names on the page (keyword.jpg) (3%)"/>
<node CREATED="1306348494422" ID="ID_583010739" MODIFIED="1306348494422" TEXT="16. Keyword in &lt;b&gt; or &lt;strong&gt; tags (2%)"/>
<node CREATED="1306348494422" ID="ID_1721920112" MODIFIED="1320776368980" TEXT="17. Keyword in List Items &lt;li&gt; on the Page (3%)"/>
<node CREATED="1306348494423" ID="ID_1571346324" MODIFIED="1306348494423" TEXT="18. Keyword in &lt;i&gt; or &lt;em&gt; Tags (2%)"/>
<node CREATED="1306348494423" ID="ID_1616495491" MODIFIED="1306348494423" TEXT="19. Keyword in the Meta Description Tag (2%)"/>
<node CREATED="1306348494424" ID="ID_1604563320" MODIFIED="1306348494424" TEXT="20. Keyword in comment tags in the HTML (1%)"/>
</node>
<node CREATED="1305957865826" ID="ID_183053818" LINK="https://adwords.google.com/select/KeywordToolExternal" MODIFIED="1305957865826" TEXT="https://adwords.google.com/select/KeywordToolExternal"/>
</node>
<node CREATED="1305960393263" FOLDED="true" ID="ID_531008456" MODIFIED="1404654939794" POSITION="right" TEXT="Meta Tags">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305958507278" FOLDED="true" ID="ID_279625399" MODIFIED="1377255008953" TEXT="Meta Description">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305958545935" ID="ID_178966359" MODIFIED="1305958547845" TEXT="keep your descriptions to 155 characters or less."/>
<node CREATED="1305958560115" ID="ID_912117825" MODIFIED="1305958562415" TEXT="This is the number of characters most search engines will display."/>
</node>
<node CREATED="1305958654275" FOLDED="true" ID="ID_132263574" MODIFIED="1377255018468" TEXT="Meta Title tags">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305958697095" ID="ID_1195963304" MODIFIED="1305958706752" TEXT="use same keywords"/>
<node CREATED="1305958682983" ID="ID_707526282" MODIFIED="1305958694031" TEXT="Google allows up to 66 characters (including spaces) for title tags, whereas some others look at up to 120 characters."/>
<node CREATED="1305958722683" ID="ID_4453912" MODIFIED="1305958723949" TEXT="The numbers change frequently; last year the general rule was no more than 70 characters."/>
<node CREATED="1305958745583" ID="ID_4951476" MODIFIED="1305958750574" TEXT="(W3C) recommends that website designers keep their titles to 64 characters or less in length."/>
<node COLOR="#0033ff" CREATED="1305958790948" ID="ID_846611818" MODIFIED="1305958797007" TEXT="There should also be unique Title tags for every page of your website.">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
</node>
<node COLOR="#0033ff" CREATED="1305958814603" ID="ID_534553681" MODIFIED="1305958828548" TEXT="Make the title as descriptive as possible and use the most important keywords">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
</node>
</node>
<node CREATED="1305958909655" FOLDED="true" ID="ID_360976909" MODIFIED="1321787563867" TEXT="Meta Robots Tag">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305958930608" ID="ID_1636745596" MODIFIED="1305958932591" TEXT="ability to specify whether search engine bots, spiders, and crawlers can index that page or follow the links appearing on that page."/>
<node COLOR="#990099" CREATED="1305958968871" ID="ID_1738295492" MODIFIED="1305958976023" TEXT="&lt;meta name=&quot;robots&quot; content=&quot;index,follow&quot;&gt;">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
</node>
<node CREATED="1305958996403" ID="ID_432012220" MODIFIED="1305958998288" TEXT="other syntaxes you can use instead of &#x201c;index.follow."/>
<node CREATED="1305959415418" FOLDED="true" ID="ID_601254259" MODIFIED="1321112897457" TEXT="content tags">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959013563" FOLDED="true" ID="ID_805392081" MODIFIED="1321110672486" TEXT="index:">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959017008" ID="ID_1250827552" MODIFIED="1305959026472" TEXT="This allows the spider to index that page"/>
</node>
<node CREATED="1305959037791" FOLDED="true" ID="ID_643417348" MODIFIED="1321112874970" TEXT="noindex">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959055820" ID="ID_512552466" MODIFIED="1305959058184" TEXT="This instructs the spider not to index the page."/>
</node>
<node CREATED="1305959067563" FOLDED="true" ID="ID_1771829018" MODIFIED="1321110677446" TEXT="follow:">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959268736" ID="ID_265253164" MODIFIED="1305959268736" TEXT="This instructs the spider to follow the links from that page and index them."/>
</node>
<node CREATED="1305959241959" FOLDED="true" ID="ID_511063543" MODIFIED="1321112896585" TEXT="nofollow:">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959259848" ID="ID_1834146279" MODIFIED="1305959259848" TEXT="This instructs the spider not to follow links from that page for indexing"/>
</node>
<node CREATED="1305959276211" FOLDED="true" ID="ID_339725661" MODIFIED="1321112895417" TEXT="all:">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959288748" ID="ID_1391130795" MODIFIED="1305959290297" TEXT="This is identical to &quot;index, follow&quot;."/>
</node>
<node CREATED="1305959297148" FOLDED="true" ID="ID_1572492565" MODIFIED="1321112893049" TEXT="none:">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1305959314694" ID="ID_1089552925" MODIFIED="1321112891116" TEXT="This is identical to &quot;noindex, nofollow&quot; telling the search engines to ignore the web page."/>
</node>
</node>
<node CREATED="1305959438452" ID="ID_926444539" MODIFIED="1305959468930" TEXT="You can include a file called robot.txt with permissions"/>
<node CREATED="1305959608727" FOLDED="true" ID="ID_562326017" MODIFIED="1321117222480" TEXT="e.g.">
<node CREATED="1305959611495" ID="ID_1493818168" MODIFIED="1305959719204" TEXT="&lt;HEAD&gt; &#xa;&#xa;&lt;Title&gt;Portland Office Stuff | Portland Office Furniture | Portland Office Interiors | Portland Office Cubicles &lt;/Title&gt; &#xa;&#xa;&lt;Meta http-equiv=&quot;Content-type&quot; content=&quot;text/html; charset=ISO-8859-1&quot;&gt;&#xa;&#xa; &lt;Meta name=&quot;keywords&quot; content=&quot;portland office furniture, portland office interiors, portland office cubicles, portland furniture installers, portland furniture repair, portland healthcare furniture, portland medical furniture, teknion, steelcase, herman miller&quot;&gt;&#xa;&#xa; &lt;Meta name=&quot;description&quot; content=&quot;Portland Office Stuff is the premier source for office systems and furniture for Portland, Gresham, and surrounding areas. We specialize in planning and outfitting your office environment from top to bottom.&quot;&gt;&#xa;&#xa; &lt;Meta name=&quot;googlebot&quot; content=&quot;index, follow&quot; /&gt;&#xa; &lt;Meta name=&quot;robots&quot; content=&quot; index,follow &quot;&gt; &#xa;&lt;Meta name=&quot;Revist-After&quot; content=&quot;7 days&quot;&gt; &#xa;&lt;Meta name=&quot;city&quot; content=&quot;Portland&quot;&gt; &#xa;&lt;Meta name=&quot;country&quot; content=&quot;United States (USA)&quot;&gt;&#xa; &lt;Meta name=&quot;state&quot; content=&quot;OR&quot;&gt;&#xa; &lt;Meta name=&quot;zip code&quot; content=&quot;97210&quot;&gt; &#xa;&lt;Meta name=&quot;subject&quot; content=&quot;Portland Office Furniture&quot;&gt;&#xa; &lt;Meta name=&quot;author&quot; content=&quot;Sean Odom&quot;&gt; &#xa;&lt;Meta name=&quot;copyright&quot; content=&quot;Portland Office Stuff&quot;&gt;&#xa; &lt;/HEAD&gt;"/>
</node>
<node CREATED="1305960252090" ID="ID_1176215559" MODIFIED="1305960254928" TEXT="&#x201c;author&#x201d; tag is the person or company name that created the content in the website."/>
</node>
