</node>
<node CREATED="1306265370696" FOLDED="true" ID="ID_1242993593" MODIFIED="1404650451283" TEXT="Step 5 - Submitting to DMOZ">
<node CREATED="1306265394980" ID="ID_199905557" MODIFIED="1306265396252" TEXT="also known as the Open Directory Project),"/>
<node CREATED="1306265407280" ID="ID_609845523" MODIFIED="1306265408397" TEXT="is important because AOL Search, AltaVista, HotBot, Lycos, and Netscape Search all increase the ranking of your site if they are listed there."/>
<node CREATED="1306265546296" FOLDED="true" ID="ID_1719888299" MODIFIED="1404650449220" TEXT="has certain rules to follow">
<font BOLD="true" NAME="SansSerif" SIZE="12"/>
<node CREATED="1306265563680" ID="ID_189972784" MODIFIED="1306265582868" TEXT="Do not submit an URL that contains only the same or similar content as other sites you may have listed in the directory."/>
<node CREATED="1306265563681" ID="ID_948145063" MODIFIED="1306265601101" TEXT="Do not submit any site with an address that redirects to another address."/>
<node CREATED="1306265623492" ID="ID_975202428" MODIFIED="1306265624750" TEXT="Do not submit sites &quot;under construction.&quot;"/>
<node CREATED="1306265636576" ID="ID_1879520003" MODIFIED="1306265638046" TEXT="Submit pornographic sites to the appropriate category under Adult."/>
<node CREATED="1306265809856" ID="ID_580870510" MODIFIED="1306265811939" TEXT="Submit non-English sites to the appropriate category under World."/>
<node CREATED="1306265813248" ID="ID_841928415" MODIFIED="1306265828455" TEXT="Don&apos;t submit sites consisting largely of affiliate links."/>
</node>
</node>
<node CREATED="1306265840900" ID="ID_958748943" MODIFIED="1306265842323" TEXT="Step 6 - Submitting to Alexa">
<node CREATED="1306266006244" ID="ID_1294518592" LINK="http://www.alexa.com/edit" MODIFIED="1306266011449" TEXT="http://www.alexa.com/edit"/>
</node>
<node CREATED="1306266042088" ID="ID_700347325" MODIFIED="1306267104346" TEXT="Step 7 - Submitting to AllCanSeek"/>
<node CREATED="1306267096949" ID="ID_562178830" MODIFIED="1306267557486" TEXT="Step 8 - Submitting to CanLinks"/>
<node CREATED="1306267266516" ID="ID_58306428" MODIFIED="1321733615770" TEXT="Step 9 - Submitting To Jayde for Business To Business Websites">
